(5, 2, '17', 'Solomon\\\'s Porch', 'solomons-porch', '\\\"Solomon\\\'s Porch\\\" is a series about a small, town square Christian Bookstore on Main Street America. The store is run by Mary Hendricks, a woman with a secretive past.', NULL, 'https://www.rediscovertelevision.com/wp-content/uploads/2022/11/Screen-Shot-2022-11-11-at-8.10.25-PM-800x450.png', NULL, NULL, NULL, 'Solomon\\\'s Porch', '\\\"Solomon\\\'s Porch\\\" is a series about a small, town square Christian Bookstore on Main Street America. The store is run by Mary Hendricks, a woman with a secretive past.', 'Originals,Series', 1, 0),
(6, 2, '', 'Covidland: The Full Interviews', 'covidland-the-full-interviews', 'New Interview Released Every Friday! Framing the World interviewed 50 high profile people for the Covidland film series. These are those full unedited interviews.', NULL, 'https://www.rediscovertelevision.com/wp-content/uploads/2022/07/Screen-Shot-2022-07-11-at-10.43.16-PM.png', NULL, NULL, NULL, 'Covidland: The Full Interviews', 'New Interview Released Every Friday! Framing the World interviewed 50 high profile people for the Covidland film series. These are those full unedited interviews.', 'New Episodes,New Releases,Originals', 1, 0),
